from src.analysis.AUserAnalyzer import AUserAnalyzerfrom src.models.user.FbUser import FbUserfrom src.helpers.LanguageClassifier import detect_langfrom src.helpers.DataManager import DataManagerclass FbUserAnalyzer(AUserAnalyzer):    def __init__(self):        self.work_skills = DataManager().work_skills        def analyze(self, user: FbUser):        self.analyze_skills(user)        self.analyze_interests(user)        def analyze_skills(self, user: FbUser):        """        Analyzes users skills according to job positions he had        :param user: FbUser object        """        skills = [0] * len(self.work_skills.get('skill_names').get('en'))        for job in user.jobs:            lang = detect_lang(job)            for i, jobs in enumerate(self.work_skills.get('jobs').get(lang, [])):                if job in jobs:                    skills[i] = skills[i] + 1        user.skills = skills    def analyze_interests(self, user):        interests = set()        if len(user.education) > 5:            interests.add('Self education')        if len(user.languages) > 4:            interests.add('Languages')            interests.add('Self education')