from src.analysis.AUserAnalyzer import AUserAnalyzerfrom src.analysis.Analyzer import Analyzerfrom src.helpers.DataManager import DataManagerfrom src.helpers.LanguageClassifier import detect_langfrom src.models.user.FbUser import FbUserclass FbUserAnalyzer(AUserAnalyzer):        def analyze_skills(self, user:FbUser):        user.skills_weights = Analyzer().analyze_skills(user.jobs)        def analyze_interests(self, user: FbUser):        interests = user.about        interests.extend(user.events)        int1 = Analyzer().analyze_skills(interests)        user.interests_from_skills_weights = [i1 + i2 for i1, i2 in zip(int1, user.skills)]                interests = user.favorite_athletes        interests.extend(user.favorite_teams)        interests.extend(user.inspirational_people)        interests.extend(user.sports)        interests.extend(user.albums)        interests.extend(user.games)        interests.extend(user.movies)        interests.extend(user.music)        interests.extend(user.television)        interests.extend(user.books)        user.interests = interests